mapper.select:
SELECT * FROM AIRLINE_INFO 
WHERE DEPART_LOC LIKE #{departLoc} 
AND ARRIVAL_LOC LIKE #{arrivalLoc}

SELECT * FROM AIRLINE_INFO
WHERE DEPART_LOC LIKE '#{departLoc} AND ARRIVAL_LOC LIKE '#{arrivalLoc}


		ORDER BY #{ticketPrice} 


		ORDER BY #{ticketPrice} DESC


		ORDER BY REGEXP_REPLACE(#{flightTime},'(\d+)시간 (\d+)분', '\1\2') as FLIGHT_TIME) 


		ORDER BY REGEXP_REPLACE(#{flightTime},'(\d+)시간 (\d+)분', '\1\2') as FLIGHT_TIME)  DESC
